    this.init();

    // No here
    this.convertType = function(results){
        for(var result in results){
            results[result]['type'] = (results[result]['type'] == 1) ? 'categoria' : 'contenido';
        }
        return results;
    }




    /// forms     
    // this.form = false;
    
    // this.init = function(){
